
public class Ascii_Value {

	/*
	 * print Ascii values of a Character
	 *  
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
			char ch='A';
			int i= ch;
			System.out.println("Ascii value of "+ch+" is-->"+i);
			
			char c='a';
			int j= c;
			System.out.println("Ascii value of "+c+" is-->"+j);
			
			char e='D';
			int k=e;
			System.out.println("Ascii value of "+e+" is-->"+k);
			
			
	}

}
