
public class alphabets_printing {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (char ch='A';ch<='Z';ch++){
			System.out.print(ch);
			System.out.print(' ');
		}
		System.out.println();
		for (char ch='a';ch<='z';ch++){
			System.out.print(ch);
			System.out.print(' ');
		}
	}

}
